import React from "react";

import Card from 'react-bootstrap/Card';

export const IconDetail = ({ data }) =>{
    return (
        <Card style={{ backgroundColor: "rgba(34, 34, 34, 0.8)",
          backdropFilter: "blur(6px) brightness(1.2)",           
            width: "300px", // Ancho específico
            margin: "0 auto", // Centrar la card horizontalmente
            boxShadow: " 0px 4px 10px rgba(0, 0, 0, 0.3)"// Agregar sombra para darle un efecto de elevación
             }}>
        <Card.Header as="h5"> <img
    src={data.image}
    alt= "icon-image"
    style={{ width: "30px", height: "30px" }} />
  </Card.Header>
        <Card.Body>
          <Card.Title>{data.title}</Card.Title>
          <Card.Text>
            With supporting text below as a natural lead-in to additional content.
          </Card.Text>
          
        </Card.Body>
      </Card>
    );
}
export default IconDetail
